<?php#必须有 $config array$config['session_start'] = true;#start session. if isn't Command Line.if (isset($_SERVER['REMOTE_ADDR'])) {	session_start();}#设置自动加载spl_autoload_register('__autoload');function __autoload($className) {	if (class_exists($className, false) || interface_exists($className, false)) {		return false;    }			if (file_exists(APPPATH."libraries/".$className.EXT)) {  		require APPPATH."libraries/".$className.EXT;  	}  }#define var pathdefine('VARPATH', dirname(APPPATH).'/var');