<?php  if (!defined('BASEPATH')) exit('No direct script access allowed');class Layout{        var $obj;    var $layout;        function Layout($layout = "layout_main")    {        $this->obj =& get_instance();        $this->layout = $layout;				$this->_loadJs = array();    }    function setLayout($layout)    {      $this->layout = $layout;    }		function loadJs($js1,$js2=null,$js3=null,$js4=null,$js5=null) 	{		if(!empty($this->_loadJs))			$tmp = $this->_loadJs;		else			$tmp = array();				if(!empty($js1)&&!in_array($js1,$tmp)) $tmp[] = $js1;		if(!empty($js2)&&!in_array($js2,$tmp)) $tmp[] = $js2;		if(!empty($js3)&&!in_array($js3,$tmp)) $tmp[] = $js3;		if(!empty($js4)&&!in_array($js4,$tmp)) $tmp[] = $js4;		if(!empty($js5)&&!in_array($js5,$tmp)) $tmp[] = $js5;				$this->_loadJs = $tmp;	}        function view($view, $data=null, $return=false)    {        $loadedData = array();		$loadedData['_loadJs'] = $this->_loadJs;        $loadedData['content_for_layout'] = $this->obj->load->view($view,$data,true);        if($return)        {            $output = $this->obj->load->view($this->layout, $loadedData, true);            return $output;        }        else        {            $this->obj->load->view($this->layout, $loadedData, false);        }    }}